mapper.select:
SELECT b.msg_type ,b.input_code ,t.*,t.tpl_params as wxTplParam,wt.title as wxTplTitle,wt.content as wxTpl FROM wx_tpl_msg_text t , wx_msg_base b, wx_tpl wt
WHERE t.base_id = b.id and t.tpl_id=wt.id_wx_tpl

	and b.input_code=#{inputcode}


	and wt.account = #{account}

ORDER BY b.create_time DESC

SELECT b.msg_type ,b.input_code ,t.* FROM wx_tpl_msg_text t , wx_msg_base b, wx_tpl wt
WHERE t.base_id = b.id and t.tpl_id=wt.id_wx_tpl

	and b.input_code=#{inputcode}


	and wt.account = #{account}

ORDER BY b.create_time DESC

mapper.delete:
DELETE FROM wx_tpl_msg_text


		ID = #{id}


		and base_id = #{baseId}
